var THREE = require('three');
var defaults = require('lodash.defaults');

var __defaultParams = {

};

function CarvableTetrahedron(params) {
	params = defaults(params, __defaultParams);

	var geometry = new THREE.BoxGeometry(1, 1, 1, 1, 1, 1);
	THREE.Mesh.call(this, geometry, params.material);
}

CarvableTetrahedron.prototype = Object.create(THREE.Mesh.prototype);

CarvableTetrahedron.prototype.onEnterFrame = function() {
	this.rotation.y = performance.now() * 0.0001;
}

module.exports = CarvableTetrahedron;