const seedRandom = require('seed-random');

module.exports = function create (key) {
  const seed = seedRandom(key);
  return function (min, max) {
    if (typeof min === 'undefined') {
      min = 1;
    }
    if (typeof max === 'undefined') {
      max = min;
      min = 0;
    }
    return seed() * (max - min) + min;
  };
};
